mapper.insert:
INSERT INTO tb_board (

) VALUES (
	  #{idx}
	, #{title}
	, #{content}
	, '관리자'
	, 0
	, IFNULL(#{noticeYn}, 'N')
	, IFNULL(#{secretYn}, 'N')
	, 'N'
	, #{insertTime}
	, NULL
	, NULL
)

mapper.update:
UPDATE tb_board
SET
	  update_time = NOW()
	, title = #{title}
	, content = #{content}
	, notice_yn = IFNULL(#{noticeYn}, 'N')
	, secret_yn = IFNULL(#{secretYn}, 'N')
	, insert_time = #{insertTime}
WHERE
	idx = #{idx}
