mapper.insert:
INSERT INTO tb_member (

) VALUES (
	  #{memNo}
	, #{memName}
	, #{sex}
	, #{birthday}
	, #{memId}
	, #{password}
	, IFNULL(#{active}, '0')
	, #{emailAddr}
	, IFNULL(#{emailYn}, '1')
	, IFNULL(#{smsYn}, '1')
	, #{phoneNo}
	, NOW()
	, NOW()
	, null
	, #{diKey}
	, #{sleepYn}
	, NOW()
)

mapper.update:
UPDATE tb_member
SET
	  pwd_modify_date = NOW()
	, password = #{password}
WHERE
	mem_no = #{memNo}
