mapper.insert:
INSERT INTO r_recruit_qna (
	  mem_no
	, r_title
	, r_content
	, r_insert_date
	, r_update_date
	, r_delete_yn
) VALUES (
	  #{memNo}
	, #{rTitle}
	, #{rContent}
	, NOW()
	, NOW()
	, 'N'
)

mapper.update:
UPDATE r_recruit_qna
SET
	  r_update_date = NOW()
	, r_title = #{rTitle}
	, r_content = #{rContent}
WHERE
	r_id = #{rId} and mem_no = #{memNo}
